package Contests;

import java.util.HashMap;


public class LeetCode_Weekly_Contest_2 {

	public void test_389_Find_the_Difference() {
		String s = "";
//		String s= "aam" ;
		String t = "";

		char findTheDifference = new _389_Find_the_Difference().findTheDifference(s, t);
		System.out.println("findTheDifference: " + findTheDifference);
	}

}

class _389_Find_the_Difference {
	public char findTheDifference(String s, String t) {
		HashMap<Character, Integer> sHM = new HashMap<Character, Integer>();

		for (int i = 0; i < s.length(); i++) {
			Character sWord = new Character(s.charAt(i));
			int num = 0;
			try {
				num = sHM.get(sWord);
			} catch (Exception e) {
//				e.printStackTrace();
			}
			if (num == 0) {
				sHM.put(sWord, new Integer(1));
			} else {
				sHM.put(sWord, new Integer(num + 1));
			}
		}

		for (int i = 0; i < t.length(); i++) {
			Character word = new Character(t.charAt(i));

			if (!sHM.containsKey(word)) {
				return word;
			}

			int num = sHM.get(word);
			if (num == 0)
				return word;
			sHM.put(word, num - 1);
		}

		return '?';
	}
}

